using System;

using GuildWars2.Overlay.Contract;

namespace GuildWars2.Spidy.Prospects
{
    public static class OverlayHandler
    {
        public static void Initialize()
        {
            OverlayManager.LoadHTML(
                @"
<!DOCTYPE HTML>
<html>
  <head>
    <title></title>
    <script src=""http://ajax.aspnetcdn.com/ajax/jquery/jquery-1.9.0.js""></script>
    <script type=""text/javascript"">
function loadItem(item)
{
  $('#item_name').text(item.name);
  loadCopperValue($('#item_buy'), item.buy);
  loadCopperValue($('#item_sell'), item.sell);
  loadCopperValue($('#item_margin'), item.margin);

  setWatchFlag(item.watchFlag);
}

function loadCopperValue(item, value)
{
  var g = Math.floor(value / 10000);
  var s = Math.floor((value % 10000) / 100);
  var c = value % 100;

  var content = '';

  content += '<span class=""gold"">';
  if (g < 1000) content += '&nbsp;';
  if (g < 100) content += '&nbsp;';
  if (g < 10) content += '&nbsp;';
  content += g;
  content += ' <span class=""currency currency-g""></span>';
  content += '</span> ';

  content += '<span class=""silver"">';
  if (s < 10) content += '0';
  content += s;
  content += ' <span class=""currency currency-s""></span>';
  content += '</span> ';

  content += '<span class=""copper"">';
  if (c < 10) content += '0';
  content += c;
  content += ' <span class=""currency currency-c""></span>';
  content += '</span>';

  $(item).html(content);
}

function setWatchFlag(setFlag)
{
  $('body').removeClass('background-watch-r');
  $('body').removeClass('background-watch-g');
  $('body').removeClass('background-watch-b');

  switch (setFlag)
  {
    case 1:
      $('body').addClass('background-watch-r');
      break;
    case 2:
      $('body').addClass('background-watch-g');
      break;
    case 3:
      $('body').addClass('background-watch-b');
      break;
    default:
      break;
  }
}
    </script>
    <style type=""text/css"">
body
{
  color: #fff;
  font-family: Arial;
  font-size: 10pt;
  font-weight: 900;
  text-shadow: 0px 0px 2px rgba(0, 0, 0, 0.9);
  background-color: rgba(0, 0, 0, 0.5);
  overflow: hidden;
}

body.background-watch-r { background-color: rgba(255, 0, 0, 0.5); }
body.background-watch-g { background-color: rgba(0, 255, 0, 0.5); }
body.background-watch-b { background-color: rgba(0, 0, 255, 0.5); }

.value
{
  font-family: Courier New;
  white-space: nowrap;
}

#item_name
{
  font-family: Arial;
}

.currency
{
  width: 15px;
  height: 15px;
  vertical-align: middle;
  display: inline-block;
  background-repeat: no-repeat;
  background-size: auto;
  background-color: transparent;
}
.currency-g { background-image: url('data:image/png;base64,'); }
.currency-s { background-image: url('data:image/png;base64,'); }
.currency-c { background-image: url('data:image/png;base64,'); }
    </style>
  </head>
  <body>
    <table>
      <tr>
        <td class=""label"">Name</td>
        <td class=""value"" id=""item_name""></td>
      </tr>
      <tr>
        <td class=""label"">Buy</td>
        <td class=""value"" id=""item_buy""></td>
      </tr>
      <tr>
        <td class=""label"">Sell</td>
        <td class=""value"" id=""item_sell""></td>
      </tr>
      <tr>
        <td class=""label"">Margin</td>
        <td class=""value"" id=""item_margin""></td>
      </tr>
    </table>
  </body>
</html>
"
                );
        }

        public static void LoadItem(ItemInfo i, int copperWatchFlag = 0)
        {
            OverlayManager.ExecuteJavascript(string.Format("loadItem({0});", 
                    string.Format("{{\"name\":\"{0}\", \"buy\":{1}, \"sell\":{2}, \"margin\":{3}, \"watchFlag\":{4}}}",
                            i.Name,
                            i.BuyPrice.ToString(),
                            i.SellPrice.ToString(),
                            i.ProfitMargin.ToString(),
                            copperWatchFlag.ToString().ToLower())));
        }
    }
}
